'use strict';const HttpCode=require('./enum/httpcode.js');class InvalidConfiguration extends Error{constructor(message,appModule,item){message||(message='Invalid configuration.');if(appModule)message+=' '+'Module: '+appModule.displayName;if(item)message+=' '+'Item: '+item;super(message);this.name='InvalidConfiguration';this.status=HttpCode.HTTP_INTERNAL_SERVER_ERROR}}class ServerError extends Error{constructor(message){super(message);this.name='ServerError';this.status=HttpCode.HTTP_INTERNAL_SERVER_ERROR}}exports.InvalidConfiguration=InvalidConfiguration;exports.ServerError=ServerError;