'use strict';const path=require('path');const Mowa=require('../server.js');const Util=Mowa.Util;const Promise=Util.Promise;module.exports={type:Mowa.Feature.INIT,load_:function(appModule,options){let bootPath=options.path?appModule.toAbsolutePath(options.path):path.join(appModule.backendPath,'bootstrap');let bp=path.join(bootPath,'**','*.js');return Promise.promisify(Util.glob)(bp,{nodir:true}).mapSeries(file=>{require(file)(appModule)})}};