'use strict';const Util=require('../util.js');const mysql=require('node-mysql-promise2');module.exports={type:Util.Feature.DBMS,load:function(appModule,dbs){Util._.forOwn(dbs,(opt,db)=>{if(!opt.connection){appModule.invalidConfig(`mysql.${db}.connection`,'Missing connection string.')}let poolByConn={};let serviceName='mysql:'+db;let service={name:serviceName,dbmsType:'mysql',dbmsSpec:opt.dbms,connectionString:opt.connection,getConnection:autoRelease=>{let pool=poolByConn[service.connectionString];if(!pool){pool=poolByConn[service.connectionString]=mysql.createPool(service.connectionString)}if(autoRelease){return pool.getConnection().then(conn=>{appModule.once('actionCompleted',()=>{conn.release()});return Promise.resolve(conn)})}return pool.getConnection()}};appModule.registerService('mysql:'+db,service)});return Promise.resolve()}};