'use strict';const Util=require('../../../../util.js'),_=Util._;module.exports={columnTypeConversions:[{desc:'Converting field type being "int(11) unsigned" and field name ending with "_time" into "datetime"',test:function(table,col){let colName=_.snakeCase(col.COLUMN_NAME);return _.endsWith(colName,'_time')&&'int(11) unsigned'===col.COLUMN_TYPE},apply:function(table,col){return{type:'datetime'}}},{desc:'Converting field type being "text" and field name ending with "_url" into "url"',test:function(table,col){let colName=_.snakeCase(col.COLUMN_NAME);return _.endsWith(colName,'_url')&&'text'===col.COLUMN_TYPE},apply:function(table,col){return{type:'url'}}},{desc:'Converting field type being "varchar(255)" and field name ending with "_url" into "url"',test:function(table,col){let colName=_.snakeCase(col.COLUMN_NAME);return _.endsWith(colName,'_url')&&'varchar(255)'===col.COLUMN_TYPE},apply:function(table,col){return{type:'url'}}}]};