'use strict';const HttpCode=require('../enum/httpcode.js');class InvalidConfiguration extends Error{constructor(message,file,item){message||(message='Invalid configuration.');if(file)message+=' '+'File: '+file;if(item)message+=' '+'Item: '+item;super(message);this.name='InvalidConfiguration';this.status=HttpCode.HTTP_INTERNAL_SERVER_ERROR}}class InternalError extends Error{constructor(message){super(message);this.name='InternalError';this.status=HttpCode.HTTP_INTERNAL_SERVER_ERROR}}exports.InvalidConfiguration=InvalidConfiguration;exports.InternalError=InternalError;